        var SPEED_SCALE = 10.0;
        var LOOK_SCALE = 0.001;
        var _lastTime = -1.0;
        var _baseAzimuth = 0.0;

        var _W=0, _A=0, _S=0, _D=0, _Q=0, _E=0;

