    var _THRESHOLD = 0.1;
    var _SPEED_ADJUST = 5.5,
        _INITIAL_SPEED_SCALAR = 6,
        _speed_scalar = _INITIAL_SPEED_SCALAR;


    var _btnPressMap = {};
    var _gamepad;
    var _hudMessageStartShowTime;
    var _hudMessageShowTime;
    var _viewerState;
    var _savepoints;
    var _nextsavepoint;

    //Nav mode toggle
    var _lockInPlane;

    var _clock = new THREE.Clock(true);

