            var value2 = Number(array[2]) + offset.z;

            array[0] = (typeof array[0] === "string") ? value0.toString() : value0;
            array[1] = (typeof array[1] === "string") ? value1.toString() : value1;
            array[2] = (typeof array[2] === "string") ? value2.toString() : value2;
