                    vector3.y = path[i+1];
                    vector3 = vector3.applyMatrix4(rotation);
                    var cp2 = mappingVectorFn(vector3.x, vector3.y);
                    path[i++] = cp2.x;
                    path[i++] = cp2.y;

                    vector3.x = path[i];
                    vector3.y = path[i+1];
                    vector3 = vector3.applyMatrix4(rotation);
                    var end = mappingVectorFn(vector3.x, vector3.y);
                    path[i++] = end.x;
                    path[i++] = end.y;
